        this.url = 'https://api.punkapi.com/v2/beers';
    }
    async getBeersFromApi() {
        this.http.get(this.url).subscribe((response) => {
            this.beers = response.slice();
        });
        console.log(this.beers);
        await this.fetchPricesForBeers();
        return this.beers;
    }
    async fetchPricesForBeers() {
        const pricePromises = [];
        for (const bear of this.beers) {
            const pricePromise = this.fetchPriceById(bear.id)
                .then(price => bear.price = price);
            pricePromises.push(pricePromise);
        }
        await Promise.all(pricePromises);
    }
    async fetchPriceById(id) {
        return new Promise((resolve) => {
            setTimeout(() => {
                const staticPrice = 15.0;
                resolve(staticPrice);
            }, 1000);
        });
    }
}
