import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class BeerService {
    constructor(http) {
        this.http = http;
        this.beers = [];
        this.url = 'https://api.punkapi.com/v2/beers';
    }
    async getArrayOfItems() {
        try {
            const response = await this.http.get(this.url).toPromise();
            this.beers = response;
            await this.fulfillMissingFields();
        }
        catch (error) {
            console.error('Error fetching beers:', error);
            throw error;
        }
    }
    async getBeers() {
        if (this.beers.length === 0) {
            await this.getArrayOfItems();
        }
        return this.beers;
    }
    async fulfillMissingFields() {
        const pricePromises = [];
        for (const bear of this.beers) {
            bear.in_cart = false;
            bear.favourite = false;
            const pricePromise = this.fetchPriceById(bear.id)
                .then(price => bear.price = price);
            pricePromises.push(pricePromise);
        }
        await Promise.all(pricePromises);
    }
    async fetchPriceById(id) {
        //simulate db call
        return new Promise((resolve) => {
            setTimeout(() => {
                const staticPrice = 15.0;
                resolve(staticPrice);
            }, 1000);
        });
    }
}
